'use strict';

const Aspect = require('mongodb/lib/operations/operation').Aspect;
const defineAspects = require('mongodb/lib/operations/operation').defineAspects;
const loadCursor = require('mongodb/lib/dynamic_loaders').loadCursor;
const OperationBase = require('mongodb/lib/operations/operation').OperationBase;
const nextObject = require('mongodb/lib/operations/common_functions').nextObject;

class HasNextOperation extends OperationBase {
  constructor(cursor) {
    super();

    this.cursor = cursor;
  }

  execute(callback) {
    const cursor = this.cursor;
    let Cursor = loadCursor();

    if (cursor.s.currentDoc) {
      return callback(null, true);
    }

    if (cursor.isNotified()) {
      return callback(null, false);
    }

    nextObject(cursor, (err, doc) => {
      if (err) return callback(err, null);
      if (cursor.s.state === Cursor.CLOSED || cursor.isDead()) return callback(null, false);
      if (!doc) return callback(null, false);
      cursor.s.currentDoc = doc;
      callback(null, true);
    });
  }
}

defineAspects(HasNextOperation, Aspect.SKIP_SESSION);

module.exports = HasNextOperation;
